import React, { Component, useState }  from "react";
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import axios from "axios";

export default function AddBook (){
    const [cover, setCover] = useState();
    const [coverName, setCoverName] = useState();
    const [pdf, setPdf] = useState();
    const [pdfName, setpdfName] = useState();

    const saveCover = (e) => {
        setCover(e.target.files[0]);
        setCoverName(e.target.files[0].name);
    };

    const uploadCover = async (e) => {
        const formData = new FormData();
        formData.append("cover", cover);
        formData.append("coverName", coverName);
        try {
            const res = await axios.post(
                "http://localhost:3000/addCover",
                formData
            );
            console.log(res);
        } catch (ex) {
            console.log(ex);
        }
    };

    
    return(
        <div >
            <h3>Add New Book</h3>
            <div>
                <Row>
                    <Col>
                        <Row>
                            <Form.Group controlId="formFileSm" className="mb-3">
                                <Form.Label>Upload book cover</Form.Label>
                                <Form.Control className="w-50" type="file" size="sm" onChange={saveCover}/>
                                <Button size="sm" variant="info" onClick={uploadCover}>Upload</Button>
                            </Form.Group>
                        </Row>
                        <Row>
                            <Form.Group controlId="formFileSm" className="mb-3">
                                <Form.Label>Upload PDF</Form.Label>
                                <Form.Control className="w-50" type="file" size="sm" onChange={saveP}/>
                                <Button size="sm" variant="info" onClick={uploadPDF-File}>Upload</Button>
                            </Form.Group>
                        </Row>
                    </Col>
                    <Col>
                        <Row>
                            <Form.Label column="sm" lg={2}>Title:</Form.Label>
                            <Col>
                                <Form.Control className="w-50" size="sm" type="text" placeholder="Title of the book" />
                            </Col>
                        </Row>
                        <Row className='mt-2'>
                            <Form.Label column="sm" lg={2}>Author:</Form.Label>
                            <Col>
                                <Form.Control className="w-50" size="sm" type="text" placeholder="Author of the book" />
                            </Col>
                        </Row>
                        <Row className='mt-2'>
                            <Form.Label column="sm" lg={2}>Publisher:</Form.Label>
                            <Col>
                                <Form.Control className="w-50" size="sm" type="text" placeholder="Publisher" />
                            </Col>
                        </Row>
                        <Row className='mt-2'>
                            <Form.Label column="sm" lg={2}>Publishing Year:</Form.Label>
                            <Col>
                                <Form.Control className="w-50" size="sm" type="text" placeholder="" />
                            </Col>
                        </Row>
                        <Row className='mt-2'>
                            <Form.Label column="sm" lg={2}>Genre:</Form.Label>
                            <Col>
                                <Form.Control className="w-50" size="sm" type="text" placeholder="" />
                            </Col>
                        </Row>
                        <Row className='mt-2'>
                            <Form.Label column="sm" lg={2}>Total pages:</Form.Label>
                            <Col>
                                <Form.Control className="w-50" size="sm" type="text" placeholder="" />
                            </Col>
                        </Row>
                        <Row className='mt-2'>
                            <Form.Label column="sm" lg={2}>Description:</Form.Label>
                            <Col>
                                <Form.Control className="w-50" size="sm" as="textarea" placeholder="" />
                            </Col>
                        </Row>
                    </Col>
                </Row>
            </div>
        </div>
    )
    
}
