import React, { Component, useState }  from "react";
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import axios from "axios";

export default function AddBook (){
    const [file, setFile] = useState();
    const [fileName, setFileName] = useState();

    const saveFile = (e) => {
        setFile(e.target.files[0]);
        setFileName(e.target.files[0].name);
    };

    const uploadFile = async (e) => {
        const formData = new FormData();
        formData.append("file", file);
        formData.append("fileName", fileName);
        try {
            const res = await axios.post(
                "http://localhost:3000/upload",
                formData
            );
            console.log(res);
        } catch (ex) {
            console.log(ex);
        }
    };

    
    return(
        <div >
            <h3>Add New Book</h3>
            <div>
                <Row>
                    <Col>
                        <Row>
                            <Col>
                            <Form.Group controlId="formFileSm" className="mb-3">
                                <Form.Label>Upload book cover</Form.Label>
                                <Form.Control className="w-25" type="file" size="sm" onChange={saveFile}/>
                            </Form.Group>
                            </Col>
                            <Col>
                            <Button size="sm" variant="info" onClick={uploadFile}>Upload</Button>
                            </Col>
                        </Row>
                    </Col>
                    <Col>
                        <Row>
                            <Form.Label column="sm" lg={2}>Title:</Form.Label>
                            <Col>
                                <Form.Control className="w-50" size="sm" type="text" placeholder="Small text" />
                            </Col>
                        </Row>
                        <Row>
                            <Form.Label column="sm" lg={2}>Small Text</Form.Label>
                            <Col>
                                <Form.Control className="w-50" size="sm" type="text" placeholder="Small text" />
                            </Col>
                        </Row>
                    </Col>
                </Row>
            </div>
        </div>
    )
    
}
