import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';


export default class BookList extends Component{
  constructor(props){
    super(props);

    this.state = {
      books: [],
    };
  }

  componentDidMount(){
    axios.get('http://localhost/5000/books/')
      .then(response => {
        this.setState({
          books: response.data,
        });
      })
      .catch(function(error){
        console.log(error);
      }
    );
  }

  render(){
  }
}
