import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Card from 'react-bootstrap/Card';
import axios from 'axios';

const Book = props => (
  <Col>
    <Card style={{ width: '15rem' }}>
      <Card.Body>
        <Card.Img variant="top" src= "http://localhost:5000/books/files/covers/Purse_2022.png" />
        <h4>{ props.book.title }</h4>
        <Card.Title>{ props.book.author }</Card.Title>
        <Card.Subtitle className="mb-2 text-muted">{ props.book.genre }</Card.Subtitle>
        {/* <Card.Link href="#"></Card.Link> */}
      </Card.Body>
    </Card>
  </Col>
)

export default class BookList extends Component{
  constructor(props){
    super(props);

    this.state = {
      books: [],
    };
  }

  componentDidMount(){
    axios.get('http://localhost:5000/books/')
      .then(response => {
        this.setState({
          books: response.data,
        });
      })
      .catch(function(error){
        console.log(error);
      }
    );
  }

  bookList(){
    return this.state.books.map(currentbook => {
      return <Book book={currentbook} key={currentbook._id}/>;
    })
  }

  render(){
    return (
      <div>
        <Row xs={1} md={5} className="g-4">
          {Array.from({ length: this.state.books.length/2 }).map((_, idx) => (
              this.bookList()
          ))}
        </Row>
        <img src="/static/covers/Purse_2022.png" alt=""/>
      </div>
    );
  }
}
